DROP TABLE IF EXISTS `dailystiuation`;
CREATE TABLE `dailystiuation`  (
  `existingConfirmed` varchar(255) CHARACTER SET utf8 COLLATE utf8_croatian_ci NOT NULL COMMENT '现存确诊',
  `cumulativeConfirmed` varchar(255) CHARACTER SET utf8 COLLATE utf8_croatian_ci NOT NULL COMMENT '累计确诊',
  `existingSuspected` varchar(255) CHARACTER SET utf8 COLLATE utf8_croatian_ci NOT NULL COMMENT '现存疑似',
  `outsideInput` varchar(255) CHARACTER SET utf8 COLLATE utf8_croatian_ci NOT NULL COMMENT '境外输入',
  `cumulativeDeath` varchar(255) CHARACTER SET utf8 COLLATE utf8_croatian_ci NOT NULL COMMENT '累计死亡',
  `cumulativeRecovery` varchar(255) CHARACTER SET utf8 COLLATE utf8_croatian_ci NOT NULL COMMENT '累计治疗',
  `date` date NOT NULL COMMENT '日期',
  `dateid` int(3) NOT NULL AUTO_INCREMENT COMMENT '用于增删改查',
  PRIMARY KEY (`dateid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 21 CHARACTER SET = utf8 COLLATE = utf8_croatian_ci ROW_FORMAT = Dynamic;

