  `suggestid` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`suggestid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 21 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

